import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Paper,
  Grid,
  Stepper,
  Step,
  StepLabel,
  CircularProgress,
  Divider,
  IconButton,
  Card,
  CardContent,
  useTheme,
  useMediaQuery,
  Alert,
} from '@mui/material';
import {
  Camera as CameraIcon,
  Refresh as RefreshIcon,
  FaceRetouchingNatural as FaceIcon,
  Check as CheckIcon,
  Close as CloseIcon,
  ArrowForward as ArrowForwardIcon,
  Info as InfoIcon,
} from '@mui/icons-material';

/**
 * Face Scanner Page that guides users through the process of scanning their face
 */
const FaceScannerPage = ({ showNotification }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // State management
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [cameraPermission, setCameraPermission] = useState(null);
  const [scanComplete, setScanComplete] = useState(false);
  const [scanData, setScanData] = useState(null);
  const [stream, setStream] = useState(null);
  const [error, setError] = useState(null);

  // Steps for the face scanning process
  const steps = [
    'Preparation',
    'Face Positioning',
    'Scanning',
    'Results',
  ];

  // Start camera when component mounts and user is on the appropriate step
  useEffect(() => {
    if (activeStep >= 1 && activeStep < 3 && !stream) {
      startCamera();
    }

    // Clean up camera resources when unmounting
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [activeStep]);

  // Start the device camera
  const startCamera = async () => {
    try {
      setLoading(true);
      setError(null);

      const constraints = {
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          facingMode: 'user'
        }
      };

      const mediaStream = await navigator.mediaDevices.getUserMedia(constraints);
      setStream(mediaStream);

      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      setCameraPermission(true);
      setLoading(false);
    } catch (err) {
      console.error('Error accessing camera:', err);
      setCameraPermission(false);
      setError('Unable to access camera. Please check permissions and try again.');
      setLoading(false);
    }
  };

  // Stop the camera
  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  // Capture a frame from the video
  const captureFrame = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Set canvas dimensions to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw the video frame on the canvas
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convert to data URL
    return canvas.toDataURL('image/png');
  };

  // Simulate face scanning process
  const performScan = async () => {
    setLoading(true);
    setError(null);

    try {
      // Capture the current frame
      const frameData = captureFrame();

      // In a real application, you would send this frame to your AI backend
      // Here we'll simulate the AI processing
      await new Promise(resolve => setTimeout(resolve, 3000));

      // Simulated face measurement results
      const mockResults = {
        faceShape: 'Oval',
        metrics: {
          faceWidth: 142,
          faceHeight: 205,
          interPupillaryDistance: 63,
          noseToEar: 97,
          templeLength: 145,
        },
        frameRecommendations: [
          { id: 101, name: 'Classic Aviator', matchPercentage: 95 },
          { id: 102, name: 'Modern Round', matchPercentage: 88 },
          { id: 103, name: 'Square Frame', matchPercentage: 82 },
        ],
        capturedImage: frameData
      };

      setScanData(mockResults);
      setScanComplete(true);
      setLoading(false);
      showNotification('Face scan completed successfully', 'success');

      // Move to results step
      setActiveStep(3);

      // Stop the camera after successful scan
      stopCamera();
    } catch (err) {
      console.error('Scan error:', err);
      setError('An error occurred during the face scan. Please try again.');
      setLoading(false);
    }
  };

  // Handle step navigation
  const handleNext = () => {
    setActiveStep((prevStep) => {
      const nextStep = prevStep + 1;

      // Special handling for scan step
      if (prevStep === 1) {
        performScan();
      }

      return nextStep;
    });
  };

  const handleBack = () => {
    // If we're at the scan step, don't go back to positioning
    // Just restart the scan
    if (activeStep === 2) {
      // Clear any error and start scanning again
      setError(null);
      performScan();
      return;
    }

    setActiveStep((prevStep) => prevStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
    setScanComplete(false);
    setScanData(null);
    stopCamera();
  };

  // View scan results and continue to shop
  const handleViewResults = () => {
    // In a real app, you would save the scan results to the user's account
    // and then navigate to the analysis page with the scan ID
    navigate('/analysis/1');
  };

  // Render step content based on active step
  const getStepContent = (step) => {
    switch (step) {
      case 0: // Preparation
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Prepare for Face Scan
            </Typography>

            <Typography paragraph>
              Follow these guidelines for the best results:
            </Typography>

            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Do:
                    </Typography>
                    <Box component="ul" sx={{ pl: 2 }}>
                      <Typography component="li">Remove glasses if you're wearing any</Typography>
                      <Typography component="li">Ensure your face is well lit</Typography>
                      <Typography component="li">Look directly at the camera</Typography>
                      <Typography component="li">Keep a neutral expression</Typography>
                      <Typography component="li">Keep your hair away from your face</Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} md={6}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Don't:
                    </Typography>
                    <Box component="ul" sx={{ pl: 2 }}>
                      <Typography component="li">Wear sunglasses or tinted lenses</Typography>
                      <Typography component="li">Scan in poor lighting conditions</Typography>
                      <Typography component="li">Move during the scanning process</Typography>
                      <Typography component="li">Tilt your head at an angle</Typography>
                      <Typography component="li">Have objects blocking parts of your face</Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            <Alert 
              severity="info" 
              sx={{ mb: 4 }}
              icon={<InfoIcon />}
            >
              This scan will use your device's camera to capture and analyze your face measurements.
              Your data is used only to recommend eyewear and will not be shared with third parties.
            </Alert>

            <Button
              variant="contained"
              onClick={handleNext}
              endIcon={<ArrowForwardIcon />}
              size="large"
            >
              Start Face Scan
            </Button>
          </Box>
        );

      case 1: // Face Positioning
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Position Your Face
            </Typography>

            <Typography paragraph>
              Center your face in the frame and follow the on-screen guidelines.
            </Typography>

            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}

            {cameraPermission === false && (
              <Alert 
                severity="warning" 
                sx={{ mb: 2 }}
                action={
                  <Button color="inherit" size="small" onClick={startCamera}>
                    Retry
                  </Button>
                }
              >
                Camera access is required for face scanning. Please check your browser permissions.
              </Alert>
            )}

            <Box 
              sx={{ 
                position: 'relative',
                width: '100%',
                height: isMobile ? '300px' : '400px',
                bgcolor: 'black',
                borderRadius: 1,
                overflow: 'hidden',
                mb: 3,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              {loading ? (
                <CircularProgress />
              ) : (
                <>
                  <video
                    ref={videoRef}
                    autoPlay
                    playsInline
                    muted
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                    }}
                  />

                  {/* Face position guide overlay */}
                  <Box
                    sx={{
                      position: 'absolute',
                      top: '50%',
                      left: '50%',
                      transform: 'translate(-50%, -50%)',
                      width: '70%',
                      height: '70%',
                      border: '2px dashed',
                      borderColor: 'primary.main',
                      borderRadius: '50%',
                      opacity: 0.7,
                      pointerEvents: 'none',
                    }}
                  />
                </>
              )}
            </Box>

            <canvas ref={canvasRef} style={{ display: 'none' }} />

            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button onClick={handleReset}>
                Cancel
              </Button>
              <Button
                variant="contained"
                onClick={handleNext}
                disabled={loading || cameraPermission === false}
                endIcon={<CameraIcon />}
              >
                Capture & Analyze
              </Button>
            </Box>
          </Box>
        );

      case 2: // Scanning
        return (
          <Box sx={{ textAlign: 'center' }}>
            <Typography variant="h5" gutterBottom>
              Analyzing Your Face
            </Typography>

            {error ? (
              <>
                <Alert severity="error" sx={{ mb: 4 }}>
                  {error}
                </Alert>

                <Button
                  variant="contained"
                  onClick={handleBack}
                  startIcon={<RefreshIcon />}
                >
                  Try Again
                </Button>
              </>
            ) : (
              <Box>
                <Box sx={{ position: 'relative', my: 6 }}>
                  <CircularProgress size={80} />
                  <Box
                    sx={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      bottom: 0,
                      right: 0,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <FaceIcon color="primary" />
                  </Box>
                </Box>

                <Typography>
                  Please hold still while we analyze your face measurements...
                </Typography>

                <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 2 }}>
                  <Button
                    onClick={handleReset}
                    startIcon={<CloseIcon />}
                  >
                    Cancel
                  </Button>
                </Box>
              </Box>
            )}
          </Box>
        );

      case 3: // Results
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Scan Complete!
            </Typography>

            {scanData ? (
              <Grid container spacing={4}>
                <Grid item xs={12} md={6}>
                  <Typography variant="h6" gutterBottom>
                    Your Face Measurements
                  </Typography>

                  <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      Face Shape: {scanData.faceShape}
                    </Typography>

                    <Divider sx={{ my: 1 }} />

                    <Typography variant="body2" gutterBottom>
                      Face Width: {scanData.metrics.faceWidth} mm
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      Face Height: {scanData.metrics.faceHeight} mm
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      Interpupillary Distance: {scanData.metrics.interPupillaryDistance} mm
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      Nose to Ear: {scanData.metrics.noseToEar} mm
                    </Typography>
                    <Typography variant="body2">
                      Temple Length: {scanData.metrics.templeLength} mm
                    </Typography>
                  </Paper>

                  <Typography variant="h6" gutterBottom>
                    Recommended Frames
                  </Typography>

                  <Paper variant="outlined" sx={{ p: 2 }}>
                    {scanData.frameRecommendations.map((recommendation, index) => (
                      <React.Fragment key={recommendation.id}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <Typography variant="body1">
                            {recommendation.name}
                          </Typography>
                          <Typography variant="body2" color="primary" fontWeight="bold">
                            {recommendation.matchPercentage}% Match
                          </Typography>
                        </Box>
                        {index < scanData.frameRecommendations.length - 1 && <Divider sx={{ my: 1 }} />}
                      </React.Fragment>
                    ))}
                  </Paper>
                </Grid>

                <Grid item xs={12} md={6}>
                  <Typography variant="h6" gutterBottom>
                    Scan Image
                  </Typography>

                  <Box
                    sx={{
                      width: '100%',
                      height: 300,
                      borderRadius: 1,
                      overflow: 'hidden',
                      mb: 3,
                    }}
                  >
                    {scanData.capturedImage && (
                      <img
                        src={scanData.capturedImage}
                        alt="Face Scan"
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                        }}
                      />
                    )}
                  </Box>

                  <Alert 
                    severity="success" 
                    icon={<CheckIcon />}
                    sx={{ mb: 3 }}
                  >
                    Your face scan was successful! You can now view detailed analysis and frame recommendations.
                  </Alert>
                </Grid>
              </Grid>
            ) : (
              <Typography>No scan data available. Please try again.</Typography>
            )}

            <Box sx={{ mt: 4, display: 'flex', justifyContent: 'space-between' }}>
              <Button
                onClick={handleReset}
                startIcon={<RefreshIcon />}
              >
                New Scan
              </Button>

              <Button
                variant="contained"
                onClick={handleViewResults}
                endIcon={<ArrowForwardIcon />}
                disabled={!scanComplete}
              >
                View Detailed Results
              </Button>
            </Box>
          </Box>
        );

      default:
        return 'Unknown step';
    }
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Face Scanning
        </Typography>

        <Stepper activeStep={activeStep} sx={{ pt: 2, pb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {getStepContent(activeStep)}
      </Paper>
    </Container>
  );
};

export default FaceScannerPage;
