import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  Box, 
  Typography, 
  Grid, 
  Card, 
  CardMedia, 
  CardContent, 
  Slider, 
  Button,
  Chip
} from '@mui/material';

// Sample eyewear data for demonstration
const SAMPLE_EYEWEAR = [
  {
    id: 101,
    name: 'Classic Aviator',
    price: 149.99,
    image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=500&q=80',
    description: 'Timeless aviator style with a modern twist. Perfect for oval and heart-shaped faces.',
    matchPercentage: 95
  },
  {
    id: 102,
    name: 'Modern Round',
    price: 129.99,
    image: 'https://images.unsplash.com/photo-1577803645773-f96470509666?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=500&q=80',
    description: 'Contemporary round frames with thin metal construction. Great for square and rectangle faces.',
    matchPercentage: 88
  },
  {
    id: 103,
    name: 'Square Frame',
    price: 159.99,
    image: 'https://images.unsplash.com/photo-1473496169904-658ba7c44d8a?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=500&q=80',
    description: 'Bold square frames with acetate construction. Ideal for round and oval faces.',
    matchPercentage: 82
  },
  {
    id: 104,
    name: 'Cat Eye Crystal',
    price: 179.99,
    image: 'https://images.unsplash.com/photo-1511499767150-a48a237f0083?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=500&q=80',
    description: 'Elegant cat-eye frames with crystal accents. Perfect for round and square faces.',
    matchPercentage: 79
  }
];

const EyewearStyler = ({ aiMeasurements }) => {
  const [eyewear, setEyewear] = useState([]);
  const [selectedFrame, setSelectedFrame] = useState(null);
  const [frameSize, setFrameSize] = useState(100);
  const [faceImage, setFaceImage] = useState(null);

  // Load eyewear data and recommendations
  useEffect(() => {
    // In a real app, you would fetch eyewear recommendations based on AI measurements
    // Here we'll use the sample data, but sort by AI match percentage if available
    let sortedEyewear = [...SAMPLE_EYEWEAR];

    if (aiMeasurements && aiMeasurements.frameRecommendations) {
      // Update match percentages based on AI recommendations
      sortedEyewear = SAMPLE_EYEWEAR.map(frame => {
        const recommendation = aiMeasurements.frameRecommendations.find(
          rec => rec.id === frame.id
        );

        return {
          ...frame,
          matchPercentage: recommendation ? recommendation.matchPercentage : frame.matchPercentage
        };
      });

      // Sort by match percentage
      sortedEyewear.sort((a, b) => b.matchPercentage - a.matchPercentage);

      // Set the first frame as selected
      setSelectedFrame(sortedEyewear[0]);
    }

    // Set face image if available from measurements
    if (aiMeasurements && aiMeasurements.capturedImage) {
      setFaceImage(aiMeasurements.capturedImage);
    }

    setEyewear(sortedEyewear);
  }, [aiMeasurements]);

  // Handle frame selection
  const handleFrameSelect = (frame) => {
    setSelectedFrame(frame);
  };

  // Handle frame size change
  const handleSizeChange = (event, newValue) => {
    setFrameSize(newValue);
  };

  return (
    <Box sx={{ textAlign: 'center', pb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Virtual Eyewear Styler
      </Typography>

      <Typography variant="body1" sx={{ mb: 3 }}>
        Try on frames virtually and see how they look on your face.
      </Typography>

      <Grid container spacing={4}>
        {/* Face with virtual try-on area */}
        <Grid item xs={12} md={6}>
          <motion.div
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <Box 
              sx={{ 
                position: 'relative',
                width: '100%',
                height: '400px',
                border: '2px solid #6E56CF',
                borderRadius: '12px',
                overflow: 'hidden',
                backgroundColor: 'rgba(0,0,0,0.2)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              {faceImage ? (
                <>
                  {/* Face image */}
                  <Box
                    component="img"
                    src={faceImage}
                    alt="Your face scan"
                    sx={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      transform: 'scaleX(-1)' // Mirror image
                    }}
                  />

                  {/* Virtual eyewear overlay */}
                  {selectedFrame && (
                    <Box
                      component="img"
                      src={selectedFrame.image}
                      alt={selectedFrame.name}
                      sx={{
                        position: 'absolute',
                        top: '40%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: `${frameSize}%`,
                        maxWidth: '80%',
                        mixBlendMode: 'multiply',
                        filter: 'drop-shadow(0 0 10px rgba(0,0,0,0.5))',
                        opacity: 0.9
                      }}
                    />
                  )}
                </>
              ) : (
                <Typography variant="body1" color="text.secondary">
                  No face scan available. Complete a face scan first.
                </Typography>
              )}
            </Box>

            {/* Size adjustment slider */}
            {selectedFrame && (
              <Box sx={{ px: 3, py: 2 }}>
                <Typography variant="body2" gutterBottom>
                  Adjust Frame Size
                </Typography>
                <Slider
                  value={frameSize}
                  onChange={handleSizeChange}
                  min={80}
                  max={120}
                  sx={{ 
                    color: '#6E56CF',
                    '& .MuiSlider-thumb': {
                      backgroundColor: '#FF1493',
                    }
                  }}
                />
              </Box>
            )}
          </motion.div>
        </Grid>

        {/* Frame selection */}
        <Grid item xs={12} md={6}>
          <Typography variant="h6" gutterBottom sx={{ textAlign: 'left', pl: 1 }}>
            Recommended Frames
          </Typography>

          <Box sx={{ maxHeight: '500px', overflow: 'auto', pr: 1 }}>
            <Grid container spacing={2}>
              {eyewear.map((frame, index) => (
                <Grid item xs={12} key={frame.id}>
                  <motion.div
                    initial={{ x: 50, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                  >
                    <Card 
                      sx={{ 
                        display: 'flex',
                        cursor: 'pointer',
                        border: selectedFrame?.id === frame.id ? '2px solid #FF1493' : '2px solid transparent',
                        boxShadow: selectedFrame?.id === frame.id 
                          ? '0 0 15px rgba(255, 20, 147, 0.3)' 
                          : '0 2px 8px rgba(0,0,0,0.1)',
                        transition: 'all 0.3s ease',
                        borderRadius: '12px',
                        overflow: 'hidden',
                        backgroundColor: 'rgba(18, 18, 38, 0.8)'
                      }}
                      onClick={() => handleFrameSelect(frame)}
                    >
                      <CardMedia
                        component="img"
                        sx={{ width: 100, height: 100, objectFit: 'cover' }}
                        image={frame.image}
                        alt={frame.name}
                      />
                      <CardContent sx={{ flex: '1 0 auto', textAlign: 'left' }}>
                        <Typography component="div" variant="h6">
                          {frame.name}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                          ${frame.price}
                        </Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                          <Chip 
                            label={`${frame.matchPercentage}% Match`}
                            size="small"
                            sx={{ 
                              backgroundColor: frame.matchPercentage > 90 
                                ? '#4CAF50' : frame.matchPercentage > 80 
                                ? '#FFC107' : '#F44336',
                              color: 'white',
                              fontWeight: 'bold'
                            }}
                          />
                        </Box>
                      </CardContent>
                    </Card>
                  </motion.div>
                </Grid>
              ))}
            </Grid>
          </Box>

          {selectedFrame && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              <Button
                variant="contained"
                sx={{
                  mt: 3,
                  py: 1.2,
                  px: 4,
                  background: 'linear-gradient(45deg, #6E56CF 30%, #FF1493 90%)',
                  boxShadow: '0 0 15px rgba(110, 86, 207, 0.5)',
                  borderRadius: '8px',
                  '&:hover': {
                    boxShadow: '0 0 20px rgba(255, 20, 147, 0.7)',
                  }
                }}
              >
                Add to Cart
              </Button>
            </motion.div>
          )}
        </Grid>
      </Grid>
    </Box>
  );
};

export default EyewearStyler;
