import React from 'react';
import { motion } from 'framer-motion';
import { 
  Box, 
  Typography, 
  Paper, 
  Grid, 
  Divider, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow,
  Chip,
  Card,
  CardContent
} from '@mui/material';
import {
  FaceRetouchingNatural as FaceIcon,
  Straighten as MeasureIcon,
  Visibility as VisibilityIcon
} from '@mui/icons-material';

const MeasurementsPanel = ({ measurements }) => {
  if (!measurements) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography variant="h5" color="text.secondary">
          No measurement data available. Please complete a face scan first.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ textAlign: 'center', pb: 4 }}>
      <Typography variant="h4" gutterBottom>
        AI Face Measurements
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
        Review your facial measurements captured by our advanced AI system.
      </Typography>

      <Grid container spacing={4}>
        {/* Measurements data */}
        <Grid item xs={12} md={7}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Paper 
              elevation={3} 
              sx={{ 
                p: 3, 
                borderRadius: '12px',
                background: 'rgba(18, 18, 38, 0.8)',
                border: '1px solid rgba(110, 86, 207, 0.3)',
                boxShadow: '0 0 20px rgba(110, 86, 207, 0.2)'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <MeasureIcon sx={{ color: '#FF1493', mr: 1 }} />
                <Typography variant="h6">
                  Key Facial Measurements
                </Typography>
              </Box>

              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Measurement</TableCell>
                      <TableCell align="right">Value (mm)</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {Object.entries(measurements.metrics).map(([key, value]) => (
                      <TableRow key={key}>
                        <TableCell component="th" scope="row">
                          {key.replace(/([A-Z])/g, ' $1')
                            .replace(/^./, str => str.toUpperCase())
                            .replace(/([a-z])([A-Z])/g, '$1 $2')}
                        </TableCell>
                        <TableCell align="right">{value}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>

              <Divider sx={{ my: 3 }} />

              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <FaceIcon sx={{ color: '#FF1493', mr: 1 }} />
                <Typography variant="h6">
                  Face Shape Analysis
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
                <Chip 
                  label={measurements.faceShape} 
                  color="primary"
                  sx={{ 
                    fontSize: '1.1rem', 
                    py: 2.5, 
                    px: 3,
                    backgroundColor: '#6E56CF',
                    '& .MuiChip-label': { px: 2 }
                  }}
                />
              </Box>

              <Typography variant="body2" color="text.secondary">
                Your face shape affects which eyewear styles will complement your features best.
                {measurements.faceShape === 'Oval' && ' Oval faces are versatile and can wear most frame styles.'}
                {measurements.faceShape === 'Round' && ' Round faces look best with angular and rectangular frames.'}
                {measurements.faceShape === 'Square' && ' Square faces are complemented by round and oval frames.'}
                {measurements.faceShape === 'Heart' && ' Heart-shaped faces look great with bottom-heavy frames.'}
                {measurements.faceShape === 'Diamond' && ' Diamond faces are suited to frames that emphasize the eyes.'}
              </Typography>
            </Paper>
          </motion.div>
        </Grid>

        {/* Recommendations */}
        <Grid item xs={12} md={5}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <Paper 
              elevation={3} 
              sx={{ 
                p: 3, 
                borderRadius: '12px',
                height: '100%',
                background: 'rgba(18, 18, 38, 0.8)',
                border: '1px solid rgba(110, 86, 207, 0.3)',
                boxShadow: '0 0 20px rgba(110, 86, 207, 0.2)'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <VisibilityIcon sx={{ color: '#FF1493', mr: 1 }} />
                <Typography variant="h6">
                  Frame Recommendations
                </Typography>
              </Box>

              <Box sx={{ mb: 3 }}>
                <Typography variant="body2" color="text.secondary" paragraph>
                  Based on your facial measurements, our AI recommends these frame types:
                </Typography>

                {measurements.frameRecommendations && measurements.frameRecommendations.map((rec, index) => (
                  <Card 
                    key={rec.id} 
                    variant="outlined" 
                    sx={{ 
                      mb: 2, 
                      backgroundColor: 'rgba(110, 86, 207, 0.1)',
                      borderRadius: '8px',
                      position: 'relative',
                      overflow: 'visible'
                    }}
                  >
                    <Box 
                      sx={{ 
                        position: 'absolute', 
                        top: -10, 
                        right: -10, 
                        backgroundColor: '#FF1493',
                        borderRadius: '50%',
                        width: 36,
                        height: 36,
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        boxShadow: '0 0 10px rgba(255, 20, 147, 0.5)'
                      }}
                    >
                      <Typography variant="body2" sx={{ color: 'white', fontWeight: 'bold' }}>
                        {rec.matchPercentage}%
                      </Typography>
                    </Box>

                    <CardContent>
                      <Typography variant="h6">
                        {rec.name}
                      </Typography>
                    </CardContent>
                  </Card>
                ))}
              </Box>

              <Box>
                <Typography variant="body2" sx={{ fontStyle: 'italic', color: 'text.secondary' }}>
                  Our AI analyzes over 30 different facial points to provide the most accurate frame recommendations
                  for your unique facial structure.
                </Typography>
              </Box>
            </Paper>
          </motion.div>
        </Grid>
      </Grid>

      {/* Face scan image */}
      {measurements.capturedImage && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.4 }}
          style={{ marginTop: '2rem' }}
        >
          <Typography variant="h6" gutterBottom>
            Your Face Scan
          </Typography>

          <Box
            sx={{
              display: 'flex',
              justifyContent: 'center',
              mt: 2
            }}
          >
            <Box
              component="img"
              src={measurements.capturedImage}
              alt="Your face scan"
              sx={{
                maxWidth: '300px',
                borderRadius: '12px',
                border: '2px solid #6E56CF',
                boxShadow: '0 0 20px rgba(110, 86, 207, 0.3)'
              }}
            />
          </Box>
        </motion.div>
      )}
    </Box>
  );
};

export default MeasurementsPanel;
