import React, { useRef, useEffect, useState } from 'react';
import AIEyewearEngine from './AIEyewearEngine';
import { motion } from 'framer-motion';

const FaceScanner3D = ({ onMeasurementsComplete }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [engine, setEngine] = useState(null);
  const [status, setStatus] = useState('Initializing...');

  useEffect(() => {
    const initEngine = async () => {
      const aiEngine = new AIEyewearEngine(videoRef.current, canvasRef.current);
      await aiEngine.initialize();
      setEngine(aiEngine);
      setStatus('Ready');
    };
    initEngine();

    return () => engine && engine.stop();
  }, [engine]);

  const handleCapture = () => {
    if (engine) {
      const data = engine.getMeasurements();
      onMeasurementsComplete({ ...data, recommendedStyle: engine.getRecommendedStyle() });
      setStatus('Captured');
    }
  };

  return (
    <motion.div className="glass-card" whileHover={{ scale: 1.05 }}>
      <h2>3D Face Scanner</h2>
      <div style={{ position: 'relative' }}>
        <video ref={videoRef} style={{ width: '100%', borderRadius: '12px' }} autoPlay />
        <canvas
          ref={canvasRef}
          style={{ position: 'absolute', top: 0, left: 0, width: '100%', opacity: 0.9 }}
          width="640"
          height="480"
        />
        <motion.div
          className="status-overlay"
          animate={{ opacity: [0.5, 1, 0.5] }}
          transition={{ repeat: Infinity, duration: 2 }}
          style={{
            position: 'absolute',
            bottom: 20,
            left: '50%',
            transform: 'translateX(-50%)',
            background: 'rgba(0, 206, 209, 0.3)',
            padding: '8px 16px',
            borderRadius: '20px',
          }}
        >
          {status}
        </motion.div>
      </div>
      <button className="nebula-button" onClick={handleCapture} style={{ marginTop: '20px' }}>
        Capture
      </button>
    </motion.div>
  );
};

export default FaceScanner3D;
