import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Button,
  Paper,
  Divider,
  Alert,
  useTheme,
  Tabs,
  Tab,
} from '@mui/material';
import {
  FaceRetouchingNatural as FaceIcon,
  AssessmentOutlined as AnalyticsIcon,
  ShoppingCartOutlined as ShopIcon,
  TipsAndUpdatesOutlined as TipsIcon,
  Gaming as GamingIcon,
  ThreeDRotation as ThreeDIcon,
} from '@mui/icons-material';

// Face Scanner Components (import the ones we need)
import FaceScanner3D from '../components/FaceScanner/FaceScanner3D';
import GamifiedMeasurement from '../components/FaceScanner/GamifiedMeasurement';
import EyewearStyler from '../components/FaceScanner/EyewearStyler';

/**
 * Face Scanner Page with 3D face scanning, measurement, and eyewear styling
 */
const FaceScannerPage = ({ showNotification }) => {
  const theme = useTheme();
  const navigate = useNavigate();

  // State for scan progress and results
  const [activeStep, setActiveStep] = useState(0);
  const [scanResults, setScanResults] = useState(null);
  const [faceAnalysis, setFaceAnalysis] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [showTips, setShowTips] = useState(true);

  // Tips for accurate scanning
  const scanningTips = [
    'Find a well-lit area with even lighting on your face',
    'Remove glasses, hats, or other accessories that may obstruct your face',
    'Look directly at the camera and maintain a neutral expression',
    'Keep your head level and centered in the frame',
    'Avoid moving during the measurement process for best results',
  ];

  // Steps in the process
  const steps = [
    'Face Scanning',
    'Measurement',
    'Analysis & Recommendations',
  ];

  // Handle tab change for different scanning modes
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  // Handle measurement complete from GamifiedMeasurement component
  const handleMeasurementComplete = (measurements) => {
    setScanResults(measurements);

    // Generate face analysis based on measurements
    // In a real app, this would come from an AI model
    const mockFaceAnalysis = {
      faceShape: {
        primaryShape: 'Oval',
        confidence: 0.92
      },
      symmetry: 0.89,
      features: ['Medium forehead', 'Balanced proportions', 'Defined cheekbones']
    };

    setFaceAnalysis(mockFaceAnalysis);

    // Move to next step
    setActiveStep(1);

    // Show success notification
    showNotification('Measurements completed successfully!', 'success');
  };

  // Handle eyewear selection
  const handleEyewearSelected = (eyewear) => {
    // In a real app, this would save the selection
    showNotification(`Selected: ${eyewear.name}`, 'success');

    // Navigate to product page
    navigate(`/products/${eyewear.id}`);
  };

  // Handle skip to next step
  const handleSkip = () => {
    if (activeStep === 0) {
      // If skipping scan, create mock data
      const mockMeasurements = {
        pupillaryDistance: 64.5,
        verticalDifference: 0.2,
        confidence: 0.85,
        timestamp: Date.now()
      };

      setScanResults(mockMeasurements);

      const mockFaceAnalysis = {
        faceShape: {
          primaryShape: 'Oval',
          confidence: 0.92
        },
        symmetry: 0.89,
        features: ['Medium forehead', 'Balanced proportions', 'Defined cheekbones']
      };

      setFaceAnalysis(mockFaceAnalysis);
    }

    setActiveStep((prevStep) => prevStep + 1);
  };

  // Handle back step
  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  // Handle finish (save & navigate)
  const handleFinish = () => {
    // In a real app, this would save all data to the user profile
    showNotification('Your measurements and preferences have been saved', 'success');

    // Navigate to shop with recommendations
    navigate('/shop');
  };

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Precision Face Scanner
        </Typography>

        <Typography variant="body1" paragraph>
          Our advanced face scanning technology uses AR and AI to measure your facial features
          with precision, ensuring the perfect eyewear fit and style recommendations.
        </Typography>

        {/* Stepper component */}
        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {/* Scanning tips */}
        {showTips && (
          <Alert 
            severity="info" 
            icon={<TipsIcon />}
            onClose={() => setShowTips(false)}
            sx={{ mb: 4 }}
          >
            <Typography variant="subtitle1" gutterBottom>
              Tips for Accurate Scanning
            </Typography>
            <Box component="ul" sx={{ m: 0, pl: 2 }}>
              {scanningTips.map((tip, index) => (
                <li key={index}>
                  <Typography variant="body2">{tip}</Typography>
                </li>
              ))}
            </Box>
          </Alert>
        )}

        {/* Step content */}
        {activeStep === 0 && (
          <Box>
            <Tabs
              value={activeTab}
              onChange={handleTabChange}
              variant="fullWidth"
              sx={{ mb: 3 }}
            >
              <Tab icon={<ThreeDIcon />} label="3D Scanner" />
              <Tab icon={<GamingIcon />} label="Gamified Measurement" />
            </Tabs>

            {activeTab === 0 && <FaceScanner3D />}
            {activeTab === 1 && <GamifiedMeasurement onMeasurementComplete={handleMeasurementComplete} />}

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
              <Button onClick={handleSkip} sx={{ mr: 1 }}>
                Skip & Use Sample Data
              </Button>
              <Button
                variant="contained"
                onClick={handleMeasurementComplete}
                disabled={activeTab === 1} // Disable for gamified measurement as it handles its own completion
              >
                Complete Measurement
              </Button>
            </Box>
          </Box>
        )}

        {activeStep === 1 && scanResults && (
          <Box>
            <EyewearStyler 
              faceAnalysis={faceAnalysis}
              onRecommendationSelected={handleEyewearSelected}
            />

            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
              <Button onClick={handleBack}>
                Back
              </Button>
              <Button
                variant="contained"
                onClick={handleFinish}
              >
                Finish & Go to Shop
              </Button>
            </Box>
          </Box>
        )}
      </Paper>

      {/* Explanatory section */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h5" gutterBottom>
          How Our Face Scanner Works
        </Typography>

        <Divider sx={{ mb: 3 }} />

        <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 3 }}>
          <Box flex={1}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <FaceIcon color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">
                Advanced AR Technology
              </Typography>
            </Box>
            <Typography variant="body2" paragraph>
              Our face scanning technology uses augmented reality to precisely map your facial features,
              measuring key points like pupillary distance, face width, and facial symmetry.
            </Typography>
          </Box>

          <Box flex={1}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <AnalyticsIcon color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">
                AI-Powered Analysis
              </Typography>
            </Box>
            <Typography variant="body2" paragraph>
              Our AI algorithms analyze your facial measurements and characteristics to determine your
              face shape, optimal frame size, and style recommendations personalized for you.
            </Typography>
          </Box>

          <Box flex={1}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <ShopIcon color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">
                Perfect Match Recommendations
              </Typography>
            </Box>
            <Typography variant="body2" paragraph>
              Based on your unique measurements, we recommend eyewear that will fit you perfectly,
              enhance your features, and match your style preferences.
            </Typography>
          </Box>
