import React, { createContext, useState, useEffect, useMemo } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import App from './App';
import reportWebVitals from './reportWebVitals';
import './index.css';

// Create context for theme mode (light/dark)
export const ThemeContext = createContext({
  themeMode: 'light',
  setThemeMode: () => {},
});

// Create context for accessibility settings
export const AccessibilityContext = createContext({
  voiceAssist: false,
  setVoiceAssist: () => {},
  reduceMotion: false,
  setReduceMotion: () => {},
  highContrast: false,
  setHighContrast: () => {},
  largeText: false,
  setLargeText: () => {},
  lineSpacing: 1,
  setLineSpacing: () => {},
  focusIndicator: 'default',
  setFocusIndicator: () => {},
  keyboardShortcuts: true,
  setKeyboardShortcuts: () => {},
  textSpacing: false,
  setTextSpacing: () => {},
  colorFilters: 'none',
  setColorFilters: () => {},
});

// Root application with contexts
const Root = () => {
  // Initialize theme mode from localStorage or default to 'light'
  const [themeMode, setThemeMode] = useState(() => {
    const savedMode = localStorage.getItem('themeMode');
    return savedMode || 'light';
  });

  // Initialize accessibility settings from localStorage
  const [voiceAssist, setVoiceAssist] = useState(() => {
    return localStorage.getItem('voiceAssist') === 'true';
  });

  const [reduceMotion, setReduceMotion] = useState(() => {
    return localStorage.getItem('reduceMotion') === 'true';
  });

  const [highContrast, setHighContrast] = useState(() => {
    return localStorage.getItem('highContrast') === 'true';
  });

  const [largeText, setLargeText] = useState(() => {
    return localStorage.getItem('largeText') === 'true';
  });

  const [lineSpacing, setLineSpacing] = useState(() => {
    const savedValue = localStorage.getItem('lineSpacing');
    return savedValue ? parseFloat(savedValue) : 1;
  });

  const [focusIndicator, setFocusIndicator] = useState(() => {
    return localStorage.getItem('focusIndicator') || 'default';
  });

  const [keyboardShortcuts, setKeyboardShortcuts] = useState(() => {
    const savedValue = localStorage.getItem('keyboardShortcuts');
    return savedValue === null ? true : savedValue === 'true';
  });

  const [textSpacing, setTextSpacing] = useState(() => {
    return localStorage.getItem('textSpacing') === 'true';
  });

  const [colorFilters, setColorFilters] = useState(() => {
    return localStorage.getItem('colorFilters') || 'none';
  });

  // Update localStorage when theme mode changes
  useEffect(() => {
    localStorage.setItem('themeMode', themeMode);

    // Apply theme class to document for global CSS variables
    document.documentElement.setAttribute('data-theme', themeMode);

    // Apply high contrast class if enabled
    if (highContrast) {
      document.documentElement.classList.add('high-contrast');
    } else {
      document.documentElement.classList.remove('high-contrast');
    }

    // Apply reduced motion preference to body
    if (reduceMotion) {
      document.body.classList.add('reduce-motion');
    } else {
      document.body.classList.remove('reduce-motion');
    }

    // Apply large text preference
    if (largeText) {
      document.body.classList.add('large-text');
    } else {
      document.body.classList.remove('large-text');
    }

    // Apply line spacing
    document.documentElement.style.setProperty('--line-height-multiplier', lineSpacing);

    // Apply focus indicator style
    document.body.setAttribute('data-focus-indicator', focusIndicator);

    // Apply text spacing
    if (textSpacing) {
      document.body.classList.add('increased-spacing');
    } else {
      document.body.classList.remove('increased-spacing');
    }

    // Apply color filters
    document.body.setAttribute('data-color-filter', colorFilters);
  }, [themeMode, highContrast, reduceMotion, largeText, lineSpacing, focusIndicator, textSpacing, colorFilters]);

  // Update localStorage when accessibility settings change
  useEffect(() => {
    localStorage.setItem('voiceAssist', voiceAssist);
    localStorage.setItem('reduceMotion', reduceMotion);
    localStorage.setItem('highContrast', highContrast);
    localStorage.setItem('largeText', largeText);
    localStorage.setItem('lineSpacing', lineSpacing);
    localStorage.setItem('focusIndicator', focusIndicator);
    localStorage.setItem('keyboardShortcuts', keyboardShortcuts);
    localStorage.setItem('textSpacing', textSpacing);
    localStorage.setItem('colorFilters', colorFilters);
  }, [voiceAssist, reduceMotion, highContrast, largeText, lineSpacing, focusIndicator, keyboardShortcuts, textSpacing, colorFilters]);

  // Create theme with light/dark mode and accessibility preferences
  const theme = useMemo(() => {
    const baseTheme = createTheme({
      palette: {
        mode: themeMode,
        ...(themeMode === 'light'
          ? {
              // Light mode colors
              primary: {
                main: highContrast ? '#0050A0' : '#3f51b5',
              },
              secondary: {
                main: highContrast ? '#D03060' : '#f50057',
              },
            }
          : {
              // Dark mode colors
              primary: {
                main: highContrast ? '#60A0FF' : '#7986cb',
              },
              secondary: {
                main: highContrast ? '#FF6090' : '#ff4081',
              },
            }),
      },
      typography: {
        fontSize: largeText ? 16 : 14,
        fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
        h1: {
          fontSize: largeText ? '2.8rem' : '2.5rem',
        },
        h2: {
          fontSize: largeText ? '2.3rem' : '2rem',
        },
        h3: {
          fontSize: largeText ? '2rem' : '1.75rem',
        },
        h4: {
          fontSize: largeText ? '1.8rem' : '1.5rem',
        },
        h5: {
          fontSize: largeText ? '1.5rem' : '1.25rem',
        },
        h6: {
          fontSize: largeText ? '1.3rem' : '1.1rem',
        },
        body1: {
          lineHeight: lineSpacing,
        },
        body2: {
          lineHeight: lineSpacing,
        },
      },
      components: {
        MuiButton: {
          styleOverrides: {
            root: {
              borderRadius: '8px',
              ...(highContrast && {
                borderWidth: '2px',
              }),
            },
          },
        },
        MuiFocusVisible: {
          styleOverrides: {
            root: {
              outline: focusIndicator === 'high' 
                ? '3px solid #F00 !important' 
                : focusIndicator === 'enhanced' 
                  ? '2px solid #0076FF !important' 
                  : undefined,
            },
          },
        },
      },
    });

    return baseTheme;
  }, [themeMode, highContrast, largeText, lineSpacing, focusIndicator]);

  return (
    <ThemeContext.Provider value={{ themeMode, setThemeMode }}>
      <AccessibilityContext.Provider
        value={{
          voiceAssist,
          setVoiceAssist,
          reduceMotion,
          setReduceMotion,
          highContrast,
          setHighContrast,
          largeText,
          setLargeText,
          lineSpacing,
          setLineSpacing,
          focusIndicator,
          setFocusIndicator,
          keyboardShortcuts,
          setKeyboardShortcuts,
          textSpacing,
          setTextSpacing,
          colorFilters,
          setColorFilters,
        }}
      >
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <BrowserRouter>
            <App />
          </BrowserRouter>
        </ThemeProvider>
      </AccessibilityContext.Provider>
    </ThemeContext.Provider>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Root />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
