import React, { useState } from 'react';
import { motion } from 'framer-motion';

const EyewearStyler = ({ aiMeasurements }) => {
  const [recommendations, setRecommendations] = useState([]);

  const eyewearDatabase = [
    { id: 'ew-001', name: 'Nebula Edge', style: 'futuristic', price: 149.99, fit: { pd: 65, bridge: 18 } },
    { id: 'ew-002', name: 'Cosmic Round', style: 'classic', price: 129.99, fit: { pd: 62, bridge: 17 } },
    { id: 'ew-003', name: 'Pulse Flyer', style: 'sporty', price: 169.99, fit: { pd: 66, bridge: 19 } },
  ];

  const generateRecommendations = () => {
    const filtered = eyewearDatabase
      .map(item => ({
        ...item,
        fitScore: calculateFitScore(aiMeasurements, item),
      }))
      .sort((a, b) => b.fitScore - a.fitScore);
    setRecommendations(filtered.slice(0, 3));
  };

  const calculateFitScore = (measurements, eyewear) => {
    const pdDiff = Math.abs(measurements.pupillaryDistance - eyewear.fit.pd);
    const bridgeDiff = Math.abs(measurements.bridgeWidth - eyewear.fit.bridge);
    return Math.max(0, 1 - (pdDiff + bridgeDiff) / 50); // Normalized to 0-1
  };

  return (
    <motion.div className="glass-card" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }}>
      <h2>Eyewear Styler</h2>
      <button className="nebula-button" onClick={generateRecommendations}>
        Discover Your Style
      </button>
      <div style={{ marginTop: '20px' }}>
        {recommendations.map((item, idx) => (
          <motion.div
            key={item.id}
            className="glass-card"
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: idx * 0.2, duration: 0.5 }}
            style={{ margin: '10px 0' }}
          >
            <h3>{item.name}</h3>
            <p>${item.price} | Fit: {(item.fitScore * 100).toFixed(0)}%</p>
            <motion.div whileHover={{ rotate: 5, scale: 1.1 }} style={{ width: '100px', height: '50px', background: '#FF1493' }} />
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default EyewearStyler;
